
function TempleAlert(a,b){this.index=typeof a==="string"?parseInt(a):a;this.msg=b;this.divId=TempleAlert.ID_PREFIX+TempleAlert.ID_SUFFIX++}TempleAlert.LEVELS=["danger","warning","info","success"];TempleAlert.ICONS=["exclamation-sign","warning-sign","info-sign","ok-sign"];TempleAlert.ALERTS_DIV=$("#_templeAlerts");TempleAlert.ID_PREFIX="_templeAlert";TempleAlert.ID_SUFFIX=0;TempleAlert.AUTO_DISMISS_TIMEOUT=5000;TempleAlert.SUCCESS_STATUS=3;TempleAlert.autoClose=function(a){window.setTimeout(function(){$(a).alert("close")},TempleAlert.AUTO_DISMISS_TIMEOUT)};TempleAlert.showAll=function(a){$.each(a,function(e,d){for(var c=0,b=d.length;c<b;c++){new TempleAlert(e,d[c]).show()}})};TempleAlert.prototype.show=function(){if(this.msg){TempleAlert.ALERTS_DIV.prepend('<div id="'+this.divId+'" class="fade in alert alert-dismissable alert-'+TempleAlert.LEVELS[this.index]+'"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button><span class="glyphicon glyphicon-'+TempleAlert.ICONS[this.index]+'" aria-hidden="true"></span> '+this.msg.replace(/\n/g,"<br />")+"</div>");if(this.index===TempleAlert.SUCCESS_STATUS){TempleAlert.autoClose("#"+this.divId)}}};TempleAlert.autoClose(".alert.alert-dismissable.alert-success");